                                                                                                                                    and
                                                                                                                                    buyers
                                                                                                                                    have
                                                                                                                                    to
                                                                                                                                    send
                                                                                                                                    either
                                                                                                                                    invoice
                                                                                                                                    data
                                                                                                                                    or
                                                                                                                                    at
                                                                                                                                    least
                                                                                                                                    reports
                                                                                                                                    in
                                                                                                                                    electronic
                                                                                                                                    format
                                                                                                                                    to
                                                                                                                                    the
                                                                                                                                    tax
                                                                                                                                    authorities
                                                                                                                                    for
                                                                                                                                    real-time
                                                                                                                                    validation
                                                                                                                                    &amp;
                                                                                                                                    auditing.
